import React, { Component } from "react";
import { Button, Modal, Table, OverlayTrigger } from "react-bootstrap";

class Cheatsheet extends Component {
  renderTooltip = (main, combo) => (
    <div
      style={{
        backgroundColor: "rgba(0, 0, 0, 0.85)",
        padding: "2px 10px",
        color: "white",
        borderRadius: 3
      }}
    >
      {main + ", " + combo}
    </div>
  );

  render() {
    const { show, onShow, onClose } = this.props;
    const items = require("./items.json");

    return (
      <>
        <Button variant="primary" onClick={onShow}>
          Cheat sheet
        </Button>

        <Modal size="lg" show={show} onHide={onClose}>
          <Modal.Header closeButton onClick={onClose}>
            <Modal.Title>Item Combinations Cheatsheet</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <p> Hover item icon to see item combinations. </p>
            <Table striped bordered hover>
              <thead>
                {items.items.map(main => {
                  return (
                    <tr key={main.name}>
                      {main.combinations.map(combo => {
                        return (
                          <th key={combo.combo}>
                            {/* <a
                              key={combo.combo}
                              data-toggle="tooltip"
                              data-placement="top"
                              title={main.name + ", " + combo.name}
                            >
                              <img src={combo.comboImg} />
                            </a> */}
                            <OverlayTrigger
                              placement="right-start"
                              delay={{ show: 250, hide: 400 }}
                              overlay={this.renderTooltip(
                                main.name,
                                combo.name
                              )}
                            >
                              <Button>
                                <img src={combo.comboImg} alt="" />
                              </Button>
                            </OverlayTrigger>
                          </th>
                        );
                      })}
                    </tr>
                  );
                })}
              </thead>
            </Table>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={onClose}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }
}

export default Cheatsheet;
